import React, { useState } from 'react';
import axios from 'axios'
import Button from 'react-bootstrap/Button';

// import './RegisterForm.css'

function RegisterForm()
{
    const [formData, setFormData] = useState
    ({
        pseudo: '',
        mail: '',
        password: '',
        password_confirm: '',
        pp: null,
    });

    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleChange = (e) => 
    {
        const {name, value} = e.target;
        setFormData
        ({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) =>
    {
        e.preventDefault();
        const data = new FormData();
        for (const [key, value] of Object.entries(formData))
        {
            data.append(key, value);
        }
        try 
        {
            const response = await axios.post('http://localhost:8000/users/register', data, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                }
            });
            alert(response.data.message);
        } 
        catch (error)
        {
            console.error(error);
        }
    };


    return (
        <>
            <div className='form-container' class="d-flex p-1 justify-content-center flex-column container text-center">
                <h2 class="d-flex justify-content-center p-2">User Registration</h2>
                <form className='registration-form' onSubmit={handleSubmit} class="p-2">
                    <div className='form-group' class="d-inline-flex input-group input-group-sm w-25 mb-2 p-1 flex-column">
                        <label htmlFor='pseudo'>Username:</label>
                        <input
                            class="form-control "
                            type='text'
                            id='pseudo'
                            name='pseudo'
                            value={formData.pseudo}
                            onChange={handleChange}
                            required>
                        </input>
                    </div>
                    <div className='form-group' class="d-inline-flex input-group input-group-sm w-25 mb-2 p-1 flex-column">
                        <label htmlFor="mail">Mail:</label>
                        <input
                            class="form-control"
                            type='email'
                            id='mail'
                            name='mail'
                            value={formData.mail}
                            onChange={handleChange}
                            required>
                        </input>
                    </div>
                    <div className='form-group' class="d-inline-flex input-group input-group-sm w-25 mb-2 p-1 flex-column">
                        <label htmlFor='password'>Password:</label>
                        <input 
                            class="form-control"
                            type='password'
                            id='password'
                            name='password'
                            value={formData.password}
                            onChange={handleChange}
                            required>
                        </input>
                    </div>
                    <div className='form-group' class="d-inline-flex input-group input-group-sm w-25 mb-2 p-1 col">
                        <label htmlFor='password_confirm'>Confirm Password:</label>
                        <input
                            class="form-control"
                            type='password'
                            id='password_confirm'
                            name='password_confirm'
                            value={formData.password_confirm}
                            onChange={handleChange}
                            required>
                        </input>
                    </div>

                    {errorMessage && <p className='error-message'>{errorMessage}</p>}
                    {successMessage && <p className='success-manage'>{successMessage}</p>}

                    <Button type='submit' className='submit-button' class="btn btn-primary rounded-md">Register</Button>
                </form>
            </div>
        </>

    );
}

export default RegisterForm;
